import React, { Component } from 'react';


export default class CreatePost extends Component {
    constructor(props) {
        super(props);

        this.onChangePostTitle = this.onChangePostTitle.bind(this);

        this.onChangeAuthor = this.onChangeAuthor.bind(this);

        this.onChangePostContent = this.onChangePostContent.bind(this);

        this.onChangePostPriority = this.onChangePostPriority.bind(this);

        this.onSubmit = this.onSubmit.bind(this);

        this.state = {
            post_title: '',
            post_author: '',
            post_content: '',
            post_priority: ''
        }
    }
    onChangePostTitle(e) {
        this.setState({
            post_title: e.target.value
        });
    }

    onChangeAuthor(e) {
        this.setState({
            post_author:e.target.value
        });
    }

    onChangePostContent(e) {
        this.setState({
            post_content:e.target.value
        });
    }

    onChangePostPriority(e) {
        this.setState({
            post_priority:e.target.value
        });
    }
    onSubmit(e) {
        e.preventDefault();

        console.log(`Form submitted:`);

        console.log(`Title: 
        ${this.state.post_title}`);

        console.log(`Post Author: 
        ${this.state.post_author}`);

        console.log(`Post Content: 
        ${this.state.post_content}`);

        console.log(`Post Priority: 
        ${this.state.post_priority}`);

        this.setState({
            todo_description: '',
            todo_responsible: '',
            todo_priority: '',
            todo_completed: false
        })
    }
    render() {
        return (
            <div style={{marginTop: 10}}>
                <h3>Create New Article</h3>
                <form onSubmit={this.onSubmit}>
                    <div className="form-group"> 
                        <label>Title: </label>
                        <input  type="text"
                                className="form-control"
                                value={this.state.post_title}
                                onChange={this.onChangePostTitle}
                                />
                    </div>

                    <div className="form-group">
                        <label>Author: </label>
                        <input 
                                type="text" 
                                className="form-control"
                                value={this.state.post_author}
                                onChange={this.onChangeAuthor}
                                />
                    </div>

                    <div className="form-group">
                        <label>: </label>
                        <input 
                                type="text" 
                                className="form-control"
                                value={this.state.post_author}
                                onChange={this.onChangeAuthor}
                                />
                    </div>


                    <div className="form-group">
                        <div className="form-check form-check-inline">
                            <input  className="form-check-input" 
                                    type="radio" 
                                    name="priorityOptions" 
                                    id="priorityLow" 
                                    value="Low"
                                    checked={this.state.todo_priority==='Low'} 
                                    onChange={this.onChangeTodoPriority}
                                    />
                            <label className="form-check-label">Low</label>
                        </div>
                        <div className="form-check form-check-inline">
                            <input  className="form-check-input" 
                                    type="radio" 
                                    name="priorityOptions" 
                                    id="priorityMedium" 
                                    value="Medium" 
                                    checked={this.state.todo_priority==='Medium'} 
                                    onChange={this.onChangeTodoPriority}
                                    />
                            <label className="form-check-label">Medium</label>
                        </div>
                        <div className="form-check form-check-inline">
                            <input  className="form-check-input" 
                                    type="radio" 
                                    name="priorityOptions" 
                                    id="priorityHigh" 
                                    value="High" 
                                    checked={this.state.todo_priority==='High'} 
                                    onChange={this.onChangeTodoPriority}
                                    />
                            <label className="form-check-label">High</label>
                        </div>
                    </div>

                    <div className="form-group">
                        <input type="submit" value="Create Todo" className="btn btn-primary" />
                    </div>
                </form>

            </div>


        )
    }
}
